using System;
using Sequentum.ContentGrabber.Api;
public class Script
{
	//See help for a definition of ContentTransformationArguments.
	public static string TransformContent(ContentTransformationArguments args)
	{
		if (args.Content.Contains("New")) {
			return DateTime.Now.AddDays(-1).ToString("yyyy-MM-dd");
		} else if (args.Content.Contains("day")) {
			return DateTime.Now.AddDays(-Convert.ToDouble(args.Content.Substring(0, args.Content.IndexOf("day", StringComparison.Ordinal)))).ToString("yyyy-MM-dd");
		} else if (args.Content.Contains("week")) {
			return DateTime.Now.AddDays(-(Convert.ToDouble(args.Content.Substring(0, args.Content.IndexOf("week", StringComparison.Ordinal))) * 7)).ToString("yyyy-MM-dd");
		}
		//Place your transformation code here.
		//This example just returns the input data
		return args.Content;
	}
}

