using System;
using System.Globalization;
using Sequentum.ContentGrabber.Api;

public class Script
{
	public static bool ConditionScript(ConditionScriptArguments args)
	{
		if (args.DataRow.GetDataValue("Posted Date") == "") {
			return false;
		}
		
		DateTime crawlDate = DateTime.Today.AddDays(-Convert.ToDouble(args.DataRow.GetDataValue("Days To Crawl")));
		DateTime postedDate = DateTime.Parse(args.DataRow.GetDataValue("Posted Date"));
		
		if (postedDate.Date.CompareTo(crawlDate) >= 0) {
			return false;
		}
		
		return true;
	}
}

using System;
using System.Collections.Generic;
using System.Data;
using Sequentum.ContentGrabber.Api;

public class Script
{        
	public static CustomDataStorage ProvideData(DataProviderArguments args)
 	{
		string configDB = "configDB";
		string competitorSiteCode = args.GlobalData.GetString("CompetitorSiteCode");
		string modValue = args.GlobalData.GetString("ModValue");
		string mod = args.GlobalData.GetString("Mod");
		DataTable crawlingList = new DataTable();
		
		IConnection connectionConfigDB = args.GetDatabaseConnection(configDB);
   		connectionConfigDB.OpenDatabase();

		try {
			string conditionQuery = "WHERE competitor_site_code = " + competitorSiteCode + " AND active_flag = 1 AND " +
				"(SELECT COUNT(1) FROM crawl_attempt(NOLOCK) WHERE attempt_url = url AND attempt_date > last_success_date) < 3 " +
				"AND CAST(last_success_date + interval AS DATE) <= CAST(GETDATE() AS DATE) ";
			
			string sql = "SELECT TOP 5 url AS crawling_url, crawling_list.country, email, password, ip, " +
				"CASE WHEN last_success_date = '1900-01-01' THEN 1 ELSE DATEDIFF(DAY, CAST(last_success_date AS DATE), CAST(GETDATE() AS DATE)) END AS days_to_crawl " +
				"FROM crawling_list(NOLOCK) INNER JOIN " +
				"(SELECT TOP 1 account_id, country FROM crawling_list(NOLOCK) " + conditionQuery + 
				"AND account_id % " + mod + " = " + modValue + " ORDER BY NEWID()) temp " +
				"ON crawling_list.account_id = temp.account_id AND crawling_list.country = temp.country " +
				"LEFT JOIN crawler_account(NOLOCK) ON crawler_account.id = crawling_list.account_id " +
				"LEFT JOIN proxy_list(NOLOCK) ON proxy_list.id = crawler_account.proxy_id " + conditionQuery + " ORDER BY NEWID()";
				
			IReader reader = connectionConfigDB.ExecuteReader(sql);
			
			crawlingList.Load(reader.GetDataReader());
			DataTableReader crawlingListReader = crawlingList.CreateDataReader();
			
			string proxyIP = "";
			
			while (crawlingListReader.Read()) {
				string insertSql = "INSERT INTO crawl_attempt(attempt_url, attempt_date) VALUES(N'" + crawlingListReader.GetString(0) + "', GETDATE())";
				
				ICommand command = connectionConfigDB.GetNewCommand();
				command.SetSql(insertSql);
				command.ExecuteNonQuery();
				
				if (!crawlingListReader.IsDBNull(4)) {
					proxyIP = crawlingListReader.GetString(4);
				}
			}
			
			if (proxyIP != "") {			
				args.GlobalData.ProxyList.AddProxyServer(proxyIP, "user-40569", "057fc56aa5679f5a");
			}
		} catch(Exception exp) {
			args.WriteDebug(exp.Message, true, DebugMessageType.Error);
			return new CustomDataStorage();
		} finally {
			connectionConfigDB.CloseDatabase();
		}
		
		return new CustomDataStorage(crawlingList);	
	}
}

using System;
using System.IO;
using System.Globalization;
using System.Collections.Generic;
using Sequentum.ContentGrabber.Api;
using Sequentum.ContentGrabber.Commands;

public class Script
{
	public static bool ExportData(DataExportArguments args)
	{
		Dictionary<string, int> crawlingCount = new Dictionary<string, int>();
		string configDatabase = "configDB";
		string targetDatabase = "";
		string targetTable = "";
		string competitorSiteCode = args.GlobalData.GetString("CompetitorSiteCode");
		string country = "";
		string crawlingUrl = "";
		string insertSQL = "";
		
		IExportReader dataReader = args.Data.GetTable();
		
		if (dataReader.Read()) {
			country = dataReader.GetStringValue("Country");
			targetDatabase = country + "_CompetitorJobsDB";
			targetTable = "competitor_jobs_" + country;

			IConnection connectionTargetDB = args.GetDatabaseConnection(targetDatabase);
       		connectionTargetDB.OpenDatabase();
			
			try {
				do {
					if (dataReader.GetStringValue("Posted Date") != "") {
						insertSQL = "INSERT INTO " + targetTable + " ([competitor_site_code],[country_id],[posted_date],[position_title],[company_name],[location],[ad_url],[industry],[career_level],[specialization],[origin],[remarks]) " +
							"SELECT " + competitorSiteCode + ",'" + country + "',@posted_date,@position_title,@company_name,@location,@ad_url,@industry,@career_level,@specialization,@origin,@remarks " +
							"WHERE NOT EXISTS (SELECT 1 FROM " + targetTable + "(NOLOCK) WHERE ad_url = '" + dataReader.GetStringValue("Url").Substring(0, Math.Min(350, dataReader.GetStringValue("Url").Length)).Replace("'", "''") + "')";
						
						if (dataReader.GetStringValue("Remarks") != "") {
							insertSQL += " AND NOT EXISTS (SELECT 1 FROM " + targetTable + "(NOLOCK) WHERE remarks = '" + dataReader.GetStringValue("Remarks").Substring(0, Math.Min(400, dataReader.GetStringValue("Remarks").Length)).Replace("'", "''") + "')";
						}
						
						ICommand insertCommand = connectionTargetDB.GetNewCommand();
						insertCommand.SetSql(insertSQL);
						
						insertCommand.AddParameterWithValue("@posted_date", DateTime.Parse(dataReader.GetStringValue("Posted Date")), CaptureDataType.DateTime);
						insertCommand.AddParameterWithValue("@position_title", dataReader.GetStringValue("Position Title").Substring(0, Math.Min(200, dataReader.GetStringValue("Position Title").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@company_name", dataReader.GetStringValue("Company Name").Substring(0, Math.Min(100, dataReader.GetStringValue("Company Name").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@location", dataReader.GetStringValue("Location").Substring(0, Math.Min(200, dataReader.GetStringValue("Location").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@ad_url", dataReader.GetStringValue("Url").Substring(0, Math.Min(350, dataReader.GetStringValue("Url").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@industry", dataReader.GetStringValue("Industry").Substring(0, Math.Min(150, dataReader.GetStringValue("Industry").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@career_level", dataReader.GetStringValue("Career Level").Substring(0, Math.Min(50, dataReader.GetStringValue("Career Level").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@specialization", dataReader.GetStringValue("Specialization").Substring(0, Math.Min(150, dataReader.GetStringValue("Specialization").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@origin", dataReader.GetStringValue("Origin").Substring(0, Math.Min(150, dataReader.GetStringValue("Origin").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.AddParameterWithValue("@remarks", dataReader.GetStringValue("Remarks").Substring(0, Math.Min(400, dataReader.GetStringValue("Remarks").Length)).Replace("'", "''"), CaptureDataType.ShortText);
						insertCommand.ExecuteNonQuery();
						
						crawlingUrl = dataReader.GetStringValue("Crawling Url");
						if (crawlingCount.ContainsKey(crawlingUrl)) {
							crawlingCount[crawlingUrl] += 1;
						} else {
							crawlingCount[crawlingUrl] = 1;
						}
					}
				} while(dataReader.Read());
				
				dataReader.Close();
				
				IConnection connectionConfigDB = args.GetDatabaseConnection(configDatabase);
				connectionConfigDB.OpenDatabase();
				
				foreach (KeyValuePair<string, int> record in crawlingCount) {
					ICommand configCommand = connectionConfigDB.GetNewCommand();
					configCommand.SetSql("UPDATE crawl_attempt SET complete_date = GETDATE(), success_flag = 1, crawl_count = " + record.Value + " WHERE id IN (SELECT TOP 1 id FROM crawl_attempt(NOLOCK) WHERE attempt_url = N'" + record.Key + "' ORDER BY attempt_date DESC)");
					configCommand.ExecuteNonQuery();
					
					configCommand.SetSql("UPDATE crawling_list SET last_success_date = GETDATE() WHERE url = N'" + record.Key + "'");
					configCommand.ExecuteNonQuery();
				}
				
				return true;
			} catch(Exception exp) {
				args.WriteDebug(exp.Message, true, DebugMessageType.Error);
				return false;
			}
		}
		
		return true;
	} 
}
